data.value:
#include <math.h>
#define buzzerPin 8
int ledler [] = {4, 5, 6, 7};
int ledsayisi = 4;
int ldr_pin = A0;
int deger = 0;
int kirmiziPin = 9;
int yesilPin = 10;
int maviPin = 11;
void setup()
{
  for (int i = 0; i < ledsayisi; i++)
  {
    pinMode(ledler[i], OUTPUT);
  }
  pinMode(13, OUTPUT);
  Serial.begin(9600);
  pinMode(kirmiziPin, OUTPUT);
  pinMode(yesilPin, OUTPUT);
  pinMode(maviPin, OUTPUT);
  pinMode(buzzerPin, OUTPUT);
}
double Termistor(double analogOkuma)
{
  double sicaklik;
  sicaklik = log(((10240000 / analogOkuma) - 10000));
  sicaklik = 1 / (0.001129148 + (0.000234125 + (0.0000000876741 * sicaklik * sicaklik )) * sicaklik );
  sicaklik = sicaklik - 273.15;
  return sicaklik;
}
void loop() {
  noTone(buzzerPin);
  int deger;
  double sicaklik;
  String yaz;
  deger = analogRead(A1);
  sicaklik = Termistor(deger);
  digitalWrite(13, HIGH);
  if (sicaklik > -18 && sicaklik <= 4)
  {
    renkAyarla(255, 255, 255); //beyaz
  }
  else if (sicaklik > 4 && sicaklik <= 9)
  {
    renkAyarla(0, 0, 255); //mavi
  }
  else if (sicaklik > 10 && sicaklik <= 17)
  {
    renkAyarla(0, 255, 255); //acik mavi
  }
  else if (sicaklik > 18 && sicaklik <= 25)
  {
    renkAyarla(0, 255, 0); //yesil
  }
  else if (sicaklik > 26 && sicaklik <= 35)
  {
    renkAyarla(80, 0, 80); //mor
  }
  else if (sicaklik > 36 && sicaklik <= 40)
  {
    renkAyarla(255, 0, 0); //kirmizi
  }
  deger = analogRead(ldr_pin);
  if (deger > 0 && deger <= 75)
  {
    digitalWrite(ledler[0], HIGH);
    digitalWrite(ledler[1], LOW);
    digitalWrite(ledler[2], LOW);
    digitalWrite(ledler[3], LOW);
    yaz = "Night";
  }
  if (deger > 75 && deger <= 255)
  {
    digitalWrite(ledler[0], HIGH);
    digitalWrite(ledler[1], HIGH);
    digitalWrite(ledler[2], LOW);
    digitalWrite(ledler[3], LOW);
    yaz = "Morning";
  }
  if (deger > 255 && deger <= 767)
  {
    digitalWrite(ledler[0], HIGH);
    digitalWrite(ledler[1], HIGH);
    digitalWrite(ledler[2], HIGH);
    digitalWrite(ledler[3], LOW);
    yaz = "Sun";
  }
  if (deger > 768 && deger <= 1023)
  {
    digitalWrite(ledler[0], HIGH);
    digitalWrite(ledler[1], HIGH);
    digitalWrite(ledler[2], HIGH);
    digitalWrite(ledler[3], HIGH);
    yaz = "hight Sun";
  }
  if (Serial.available())
  {
    int a = Serial.read();
    if (a == '0')
    {
      Serial.println(yaz);
      digitalWrite(13, LOW);
      tone(buzzerPin, 440);
      delay(100);
    }
    else if (a = '1')
    {
      Serial.println(sicaklik);
      digitalWrite(13, LOW);
      tone(buzzerPin, 340);
      delay(100);
    }
  }
}
void renkAyarla(int kirmizi, int yesil, int mavi)
{
  kirmizi = 255 - kirmizi;
  yesil = 255 - yesil;
  mavi = 255 - mavi;
  analogWrite(kirmiziPin, kirmizi);
  analogWrite(yesilPin, yesil);
  analogWrite(maviPin, mavi);
}
